// example of how to set field behavior on selected transition
setData(
        task.get(),
        new com.netgrif.application.engine.workflow.web.responsebodies.DataSet([
                "taskRef_3"    : new com.netgrif.application.engine.petrinet.domain.dataset.TaskField(behaviors: createBehavior("taskRef_3", com.netgrif.application.engine.petrinet.domain.dataset.logic.FieldBehavior.EDITABLE, "6")),
                "password_data": new com.netgrif.application.engine.petrinet.domain.dataset.TaskField(behaviors: createBehavior("password_data", com.netgrif.application.engine.petrinet.domain.dataset.logic.FieldBehavior.EDITABLE, task.get().transitionId))
        ])
)

